#include <stdio.h>
int isArmstrong(int num) {
    int originalNum, remainder, result = 0, n = 0;
    originalNum = num;
    while (originalNum != 0) {
        originalNum /= 10;
        n++;
    }
    originalNum = num;
    while (originalNum != 0) {
        remainder = originalNum % 10;
        int power = 1;
        for(int i = 0; i < n; i++) power *= remainder;
        result += power;
        originalNum /= 10;
    }
    return (result == num);
}
int main() {
    int num;
    printf("Enter a number to check if it is Armstrong: ");
    scanf("%d", &num);
    if (isArmstrong(num)) printf("%d is an Armstrong number.\n", num);
    else printf("%d is not an Armstrong number.\n", num);
    printf("Armstrong numbers from 1 to 500: ");
    for(int i = 1; i <= 500; i++) if (isArmstrong(i)) printf("%d ", i);
    return 0;
}

#include <stdio.h>
int main() {
    int num, rev = 0, temp, isPrime = 1, i;
    // Check prime number
    printf("Enter a number to check if it is prime: ");
    scanf("%d", &num);
    for(i = 2; i <= num / 2; i++) {
        if (num % i == 0) {
            isPrime = 0;
            break;
        }
    }
    if (num <= 1) isPrime = 0;
    if (isPrime) printf("%d is a prime number.\n", num);
    else printf("%d is not a prime number.\n", num);
    // Reverse number
    printf("Enter a number to reverse: ");
    scanf("%d", &num);
    temp = num;
    while (temp != 0) {
        rev = rev * 10 + temp % 10;
        temp /= 10;
    }
    printf("Reverse of %d is %d\n", num, rev);
    return 0;
}

